        MENU_HEIGHT = (MAX_MENU_ITEM + 1) * MessageFont,
        MENU_MIN_WIDTH = 50,
        MENU_FRAME_WIDTH = 10,
        MENU_FRAME_HEIGHT = 10,
        MENU_ITEM_SPACE = 2,
        MENU_ITEM_HEIGHT = MessageFont + MENU_ITEM_SPACE,
    } ;

