}
//
// 光盘机里是否有媒体？
//
int CDAudio::MediaChk()
{
    char    result[128];
    if (!MciStatus("media present", result, sizeof(result)))
        return -1;
    if (stricmp(result, "true") == 0)
        return 0;
    return 1;
}
//
// 开启光盘机
//
bool CDAudio::Open(CWindow *win)
{
    CMci::Open(win);

    if (!MciOpen())
        return false;

    if (!MciSet("time format tmsf")) {
        MciClose();
        return false;
    }

    bool chkOk = false;

    do {
        switch (MediaChk()) {
          case -1:
            MciClose();
            return false;

          case 0:
            chkOk = true;
            break;

          default:
#ifdef  notdef      //若光盘机中没有CD而出现错误时 #ifdef -&gt; #ifndef
                    // 娫堘偄側偄偐丄僨乕僞僩?僢僋偵乽幆暿梡僼傽僀?乿傪擖傟偰偍偄偰
                    // 偦傟傪僠僃僢僋偡傞偲椙偄偱偟傚偆
            if (Window-&gt;MessageBox(ApplicationTitle "傪俠俢僪?僀僽偵擖傟偰偔偩偝偄丅",
                ApplicationTitle, MB_RETRYCANCEL) == IDCANCEL) {
                MciClose();
                return false;
            }
            break;
#else
            MciClose();
            return false;
#endif
        }
    } while (!chkOk) ;

    IsOpen = true;

    return true;
}
//
// 关闭光盘机
//
bool CDAudio::Close()
{
    if (IsOpen) {
        Stop();
        MciClose();
    }
    IsOpen = false;
    return true;
}
//
// 开始播放CD（只播放指定乐曲）
//
bool CDAudio::Play(int no)
{
    if (!IsOpen)
        return false;

    Stop();
    PlayTrack = no;

    no++;       // 这里是以MixedCD为基准，所以播放1首乐曲

    // 检查乐曲长度
    // 设定只播放此范围

    char    request[64];
    sprintf(request, "length track %d", no);

    char    result[128];
    if (!MciStatus(request, result, sizeof(result)))
        return false;

    sprintf(request, "from %d:00:00:00 to %d:%s", no, no, result);
    if (!MciPlay(request))
        return false;
    return true;
}
//
// 开始播放CD（若设定档名一定会error）
//
bool CDAudio::Play(const char *)
{
    return false;
}
//
// 重新播放CD
//
bool CDAudio::Replay()
{
    return Play(PlayTrack);
}
//
// 停止播放CD
//
bool CDAudio::Stop()
{
    if (!IsOpen)
        return false;

    if (PlayTrack &gt; 0) {
        MciStop();
        PlayTrack = 0;
    }
    return true;
}
//
// 播放Wave
//
bool WaveOut::Play(const char *name)
{
    Stop();

    char    fullpath[_MAX_PATH];
    FullPath(fullpath, name);

    if (!MciOpen("waveaudio", fullpath))
        return false;

    if (!MciPlay(""))
        return false;

    playing = true;

    return true;
}
//
// 播放Wave（非实作）
//
bool WaveOut::Play(int)
{
    return false;
}
//
// 停止播放Wave
//
bool WaveOut::Stop()
{
    if (playing) {
        playing = false;
        MciStop();
        MciClose();
    }
    return true;
}
