        { "七", PLAYER | (6 &lt;&lt; 8) },
        { "八", PLAYER | (7 &lt;&lt; 8) },
        { "九", PLAYER | (8 &lt;&lt; 8) },
        { "１", ENEMY | (0 &lt;&lt; 8) },
        { "２", ENEMY | (1 &lt;&lt; 8) },
        { "３", ENEMY | (2 &lt;&lt; 8) },
        { "４", ENEMY | (3 &lt;&lt; 8) },
