        { "９", ENEMY | (8 &lt;&lt; 8) },
        { "Ⅰ", OBJECT | (0 &lt;&lt; 8) },
        { "Ⅱ", OBJECT | (1 &lt;&lt; 8) },
        { "Ⅲ", OBJECT | (2 &lt;&lt; 8) },
        { "Ⅳ", OBJECT | (3 &lt;&lt; 8) },
        { "Ⅴ", OBJECT | (4 &lt;&lt; 8) },
        { "Ⅵ", OBJECT | (5 &lt;&lt; 8) },
        { "Ⅶ", OBJECT | (6 &lt;&lt; 8) },
        { "Ⅷ", OBJECT | (7 &lt;&lt; 8) },
        { "Ⅸ", OBJECT | (8 &lt;&lt; 8) },
    } ;
    for (int i=0; i&lt;(sizeof(table) / sizeof(table[0])); i++) {
